mapper.update:
update board set b_title=#{b_title},b_pwd=#{b_pwd},
b_content=#{b_content},b_fname=#{b_fname},b_type=#{b_type} where
b_number=#{b_number}

mapper.select:
select re_number,re_content,re_regdate,re_updatedate,b_number,r.c_id,c_name
from reply r,customer c where r.c_id=c.c_id and b_number=#{b_number}
order by re_number
